// 1. ตั้งค่า Config (แก้ Path ตรงนี้ที่เดียว)
var basePath = "D:/LE380/mini-MQTT/";
var filenamePrefix = "data_";

// 2. สร้างชื่อไฟล์ตามวันที่ปัจจุบัน (เช่น data_13-12-2025.csv)
var now = new Date();
var day = String(now.getDate()).padStart(2, '0');
var month = String(now.getMonth() + 1).padStart(2, '0');
var year = now.getFullYear();
var dateString = day + "-" + month + "-" + year;
var fullFilename = basePath + filenamePrefix + dateString + ".csv";

// ตั้งค่าชื่อไฟล์ให้ message นี้
msg.filename = fullFilename; 

// 3. เตรียมข้อมูล Data Row (เวลา, Temp, Humid, Light)
var timeString = now.toLocaleTimeString(); // เวลาปัจจุบัน
var temp = msg.payload.temp.toFixed(2) || 0;
var humid = msg.payload.humid.toFixed(2) || 0;
var light = msg.payload.light.toFixed(2) || 0;

// สร้างบรรทัดข้อมูล: "14:30:05, 28.5, 60, 500"
var dataRow = timeString + "," + temp + "," + humid + "," + light + "\n";

// 4. เช็ค Logic: เป็นไฟล์ใหม่หรือไม่?
var lastFile = flow.get("currentFilename"); // ไปดูความจำเดิม

if (fullFilename !== lastFile) {
    // --- กรณี: เปลี่ยนวันใหม่ หรือ เพิ่งเริ่มโปรแกรม ---

    // อัปเดตความจำว่าเป็นชื่อไฟล์ใหม่แล้วนะ
    flow.set("currentFilename", fullFilename);

    msg.payload = { payload: dataRow, filename: fullFilename };

    return [msg]; 

} else {
    // --- กรณี: ไฟล์เดิม วันเดิม ---

    // ส่งแค่ Data อย่างเดียว
    msg.payload = dataRow;
    return [msg];
}

// 1. รับค่ารายชื่อไฟล์มาจาก Command Line
var fileListString = msg.payload;

// ถ้าไม่มีข้อมูล ให้จบการทำงาน
if (!fileListString) return msg;

// 2. แยกบรรทัดและทำความสะอาด
var files = fileListString.split("\n"); // ใช้ \n รองรับทั้ง Windows/Linux

var options = [];

files.forEach(function(filename) {
    // ลบช่องว่างหัวท้าย
    filename = filename.trim();

    // เงื่อนไขการคัดเลือกไฟล์:
    // 1. ต้องไม่เป็นค่าว่าง
    // 2. ต้องลงท้ายด้วย .csv
    // 3. (สำคัญ) ต้องขึ้นต้นด้วย "data_" เท่านั้น (เพื่อตัดไฟล์ ai_log ทิ้ง)
    if (filename.length > 0 && filename.endsWith(".csv") && filename.startsWith("data_")) {
        options.push(filename);
    }
});

// 3. เรียงลำดับจาก "มากไปน้อย" (ไฟล์วันที่ 14 จะขึ้นก่อน 13)
options.sort().reverse();

// 4. ส่งไปให้ Dropdown แสดงผล
msg.options = options;

// (แถม) ตั้งค่า Default ให้เลือกไฟล์บนสุดเสมอ (ไฟล์ล่าสุด)
if (options.length > 0) {
    msg.payload = options[0]; 
}

return msg;

// ตั้งค่า Path โฟลเดอร์หลัก (ต้องแก้ตรงนี้ให้ตรงกับเครื่องคุณ)
var basePath = "D:/LE380/mini-MQTT/";

// เอาชื่อไฟล์ที่เลือกจาก Dropdown มาต่อท้าย
msg.filename = basePath + msg.payload;

return msg;

// ดึงชื่อไฟล์ที่ฝากไว้ใน Memory (flow variable)
var filename = flow.get("selected_csv");

// ถ้ายังไม่เคยเลือกไฟล์เลย ให้แจ้งเตือนและหยุดทำงาน
if (!filename) {
    node.warn("กรุณาเลือกไฟล์ก่อนกดปุ่ม");
    msg.payload = "Please select a file first!";
    // return null เพื่อไม่ให้ส่งไปทำงานต่อ
    return null;
}

// ถ้ามีชื่อไฟล์ ก็ส่งต่อไปทำงานต่อได้เลย
msg.payload = filename;
return msg;

